import React from 'react';
import { Grid, Paper, Avatar, Typography, TextField, Button, Radio, RadioGroup, FormControlLabel, FormControl, FormLabel, Checkbox } from '@material-ui/core';
import AddCircleOutlineIcon from '@material-ui/icons/AddCircleOutline';
import { NavLink } from 'react-router-dom';
import { Formik, Field, Form, ErrorMessage } from 'formik';
import * as Yup from 'yup';

const Signup = () => {
    const paperStyle = { padding: "30px 20px", width: 340, margin: "80px auto" }
    const avatarStyle = { backgroundColor: '#1c4c74' }
    const marginTop = { marginTop: 20 }
    const initialValues = {
        name: '',
        email: '',
        gender: '',
        phoneNumber: '',
        password: '',
        confirmPassword: '',
        termsAndConditions: false,
    };
    const onSubmit = (values, props) => {
        console.log(values);
    }
    const validationSchema = Yup.object().shape({
        name: Yup.string().required("Required")
    })
    return (
        <Grid>
            <Paper elevation={20} style={paperStyle}>
                <Grid align='center'>
                    <Avatar style={avatarStyle}>
                        <AddCircleOutlineIcon />
                    </Avatar>
                    <h2>Sign Up</h2>
                    <Typography variant='caption'>Please fill the details to create an account</Typography>
                </Grid>
                <Formik initialValues={initialValues} validationSchema={validationSchema} onSubmit={onSubmit}>
                    {(props) => (
                        <Form>

                            <Field as={TextField} fullWidth name='name' label='Name' placeholder='Enter Your Name'
                                helperText={<ErrorMessage name='name' />} />
                            <Field as={TextField} fullWidth name='email' label='Email' placeholder='Enter Your Email' />
                            <FormControl style={marginTop}>
                                <FormLabel id="demo-radio-buttons-group-label">Gender</FormLabel>
                                <Field as={RadioGroup} name="gender" aria-labelledby="demo-radio-buttons-group-label"
                                    style={{ display: 'initial' }}>
                                    <FormControlLabel value="female" control={<Radio />} label="Female" />
                                    <FormControlLabel value="male" control={<Radio />} label="Male" />
                                    <FormControlLabel value="other" control={<Radio />} label="Other" />
                                </Field>
                            </FormControl>
                            <Field as={TextField} fullWidth name='phoneNumber' label='Phone Number' placeholder='Enter Your 10-digit Number' />
                            <Field as={TextField} fullWidth name='password' label='Password' placeholder='Create Your Password' />
                            <Field as={TextField} fullWidth name='confirmPassword' label='Confirm Password' placeholder='Confirm Your Password' />
                            <FormControlLabel
                                control={
                                    <Field as={Checkbox}

                                        name="termsAndConditions"
                                        color="secondary"
                                    />
                                }
                                label=" I accept the terms and conditions"
                            />
                            <Button type='submit' variant='contained' color='primary' fullWidth>Sign Up</Button>
                            <Typography style={{ paddingTop: 20, marginLeft: 110 }}> Back to {" "}
                                <NavLink to='/' >
                                    Sign In
                                </NavLink>
                            </Typography>

                        </Form>
                    )}
                </Formik>


            </Paper>
        </Grid>
    )

}


export default Signup;
